import { Link} from "react-router-dom";
import { useEffect, useState } from "react";
// import City from './City'
// import Links from './Links'
// import Incidents from './Incidents'

const API_URL =
  "https://api.846policebrutality.com/api/incidents?&filter%5Bstate%5D=";

export default function App() {
  const [link, setLink] = useState();
  const [searchString, setSearchString] = useState("New York");
  const [results, setResults] = useState([]);
  const getData = async () => {
    const response = await fetch(API_URL + searchString);
    const data = await response.json();
    setResults(data.data);
  };
  useEffect(() => {
    getData();
  }, [setLink]);

  return (
    <div className="App">
      <h1>See info for city</h1>
      {/* <City data={data[currentCity]}/>
    <button onClick={() => setCurrentCity((prevState) => prevState + 1)}>
      Next City </button>
      ) */}
      {results.length &&
        results.map((ele, i) => (
          <Link to={`/details/${ele.id}`}>{ele.title}</Link>

        ))}
    </div>
  );
}
