import React from 'react'
import './EventData.css'
import Form from './Form';
import { useState, useEffect } from 'react';
import ReactPlayer from "react-player/lazy";
export default function EventData({searchString}) {

    const API_URL =
    "https://raw.githubusercontent.com/2020PB/police-brutality/data_build/all-locations.json";
    const [link, setLink] = useState();

    const [results, setResults] = useState([]);
    const getData = async () => {
        // const {id} = props.match.params;
        try {
        const response = await fetch(API_URL + searchString);
        const data = await response.json();
        setResults(data.data);
        console.log(data.data)
        }catch(err){
            console.log(err);
        }
      };


      useEffect(() => {
        getData(searchString);
      }, [searchString]);
    return (
        <div>{    
           results.length &&

            results.map((ele, i) => {
                return (
              <div className="d3">
                  <p className="urCity">{ele.city}</p>
                  <p>{ele.title}</p>


              <a href={ele.links}  rel='noopener'>{ele.links}alt="404"</a>

              </div>
        )})

}</div>)
}
