import { Link} from "react-router-dom";
import { useEffect, useState } from "react";
import Form from './Form'
// import Links from './Links'
// import Incidents from './Incidents'

const API_URL =
  "https://api.846policebrutality.com/api/incidents?&filter%5Bstate%5D=";

export default function App() {

  function getData(val) {
    setInputdata(val.target.value)

    console.log(val)
}




  const [link, setLink] = useState();
  const [searchString, setSearchString] = useState(val);
  const [results, setResults] = useState([]);
  const getDatas = async () => {
    const response = await fetch(API_URL + searchString);
    const data = await response.json();
    setResults(data.data);
    console.log(data.data)
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setSearchString(searchString)
  }
  useEffect(() => {
    getData();
  }, [setLink]);

  return (
    <div className="App">
      <Form onSubmit={handleSubmit}/>

      {results.length &&
        results.map((ele, i) => (
          <div className="d3">

          <Link to={`/details/${ele.id}`}>{ele.links}</Link>
          </div>
        ))}

    </div>
  );
}
